import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Container } from 'react-bootstrap';
import AdminDashboard from './components/admin/AdminDashboard';
import WarehouseDashboard from './pages/WarehouseDashboard';
import DeliveryDashboard from './pages/DeliveryDashboard';
import LoginPage from './pages/LoginPage';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in (mock for now)
    const savedUser = localStorage.getItem('oox_user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('oox_user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('oox_user');
  };

  if (loading) {
    return (
      <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </Container>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route 
            path="/login" 
            element={
              user ? <Navigate to={`/${user.role}`} /> : <LoginPage onLogin={handleLogin} />
            } 
          />
          <Route 
            path="/admin" 
            element={
              user?.role === 'admin' ? 
                <AdminDashboard /> : 
                <Navigate to="/login" />
            } 
          />
          <Route 
            path="/warehouse" 
            element={
              user?.role === 'warehouse' ? 
                <WarehouseDashboard user={user} onLogout={handleLogout} /> : 
                <Navigate to="/login" />
            } 
          />
          <Route 
            path="/delivery" 
            element={
              user?.role === 'delivery' ? 
                <DeliveryDashboard user={user} onLogout={handleLogout} /> : 
                <Navigate to="/login" />
            } 
          />
          <Route 
            path="/" 
            element={
              user ? <Navigate to={`/${user.role}`} /> : <Navigate to="/login" />
            } 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
